[database]
host          = ${DB_HOST}
username      = ${DB_USERNAME}
password      = ${DB_PASSWORD}
dbname        = ${DB_NAME}
tables_prefix = ${DB_PREFIX}
port          = ${DB_PORT}
charset       = "utf8"

[General]
salt          = "#SECRET_TOKEN"
session_save_handler = dbtable

[mail]
transport     = ${MAIL_TRANSPORT}     ; 'smtp' or empty (default)
