type          = ${SMTP_AUTH_METHOD}   ; 'Plain', 'Login', 'Crammd5' or empty (default)
encryption    = ${SMTP_ENCRYPTION}    ; 'ssl', 'tls', or empty (default)
username      = ${SMTP_USERNAME}
password      = ${SMTP_PASSWORD}

